    private void BSalarioBrutoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BSalarioBrutoActionPerformed
        // TODO add your handling code here:
        double sbruto;
        int cod_empleado = Integer.parseInt(txtcodigo.getText());
        String nombres = txtnombre.getText();
        double horas_trab = Double.parseDouble(txthorastrabajadas.getText());
        double valor_hora = Double.parseDouble(txtvalorhora.getText());
        double porcent_retenc = Double.parseDouble(txtporcentajeretencion.getText());

        Empleado empleado = new Empleado(cod_empleado,nombres,horas_trab,valor_hora,porcent_retenc);
        sbruto = empleado.SalarBrut();
        txtsalariobruto.setText(String.valueOf(sbruto));
    }//GEN-LAST:event_BSalarioBrutoActionPerformed

    private void BSalarioNetoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BSalarioNetoActionPerformed
        // TODO add your handling code here:
        double snet;
        int cod_empleado = Integer.parseInt(txtcodigo.getText());
        String nombres = txtnombre.getText();
        double horas_trab = Double.parseDouble(txthorastrabajadas.getText());
        double valor_hora = Double.parseDouble(txtvalorhora.getText());
        double porcent_retenc = Double.parseDouble(txtporcentajeretencion.getText());

        Empleado empleado = new Empleado(cod_empleado,nombres,horas_trab,valor_hora,porcent_retenc);
        snet = empleado.SalarNet();
        txtsalarioneto.setText(String.valueOf(snet));
    }//GEN-LAST:event_BSalarioNetoActionPerformed

