    private void BareaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BareaActionPerformed
        // TODO add your handling code here:
        double areaTrian;
        double lado = Double.parseDouble(txtlado.getText());
        TrianguloEquilatero triangulo = new TrianguloEquilatero(lado);
        areaTrian = triangulo.calcularArea();
        txtarea.setText(String.valueOf(areaTrian));
    }//GEN-LAST:event_BareaActionPerformed

    private void BperimetroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BperimetroActionPerformed
        // TODO add your handling code here:
        double perimeTrian;
        double lado = Double.parseDouble(txtlado.getText());
        TrianguloEquilatero triangulo = new TrianguloEquilatero(lado);
        perimeTrian = triangulo.calcularPerimetro();
        txtperimetro.setText(String.valueOf(perimeTrian));
    }//GEN-LAST:event_BperimetroActionPerformed

    private void BalturaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BalturaActionPerformed
        // TODO add your handling code here:
        double altura;
        double lado = Double.parseDouble(txtlado.getText());
        TrianguloEquilatero triangulo = new TrianguloEquilatero(lado);
        altura = triangulo.calcularAltura();
        txtaltura.setText(String.valueOf(altura));
    }//GEN-LAST:event_BalturaActionPerformed

